import { expect } from 'chai';
import { Transactions } from '../src';

describe('Transaction Creation', () => {
  it('should be able to create an ETH tx', async () => {
    const rawEthTx = {
      value: 3896000000000000,
      to: '0x37d7B3bBD88EFdE6a93cF74D2F5b0385D3E3B08A',
      data:
        '',
      gasPrice: 20000000000
    };
    const { value, to, data, gasPrice } = rawEthTx;
    const recipients = [{ address: to, amount: value }];
    const cryptoTx = await Transactions.create({
      chain: 'ETH',
      recipients,
      gasPrice,
      nonce: 0,
      data
    });
    const expectedTx =
      '';
    expect(cryptoTx).to.equal(expectedTx);
  });

  it('should be able to create an ERC20 tx', async () => {
    const rawEthTx = {
      value: 3896000000000000,
      to: '0x37d7B3bBD88EFdE6a93cF74D2F5b0385D3E3B08A',
      gasPrice: 20000000000
    };
    const { value, to, gasPrice } = rawEthTx;
    const recipients = [{ address: to, amount: value }];
    const cryptoTx = await Transactions.create({
      chain: 'ERC20',
      recipients,
      gasPrice,
      nonce: 0,
      tokenAddress: '0x692a70d2e424a56d2c6c27aa97d1a86395877b3a'
    });
    const expectedTx =
      'f867808504a817c8008094692a70d2e424a56d2c6c27aa97d1a86395877b3a80b844a9059cbb00000000000000000000000037d7b3bbd88efde6a93cf74d2f5b0385d3e3b08a000000000000000000000000000000000000000000000000000dd764300b80001c8080';

    expect(cryptoTx).to.equal(expectedTx);
  });
});
